window.addEventListener("load",()=>{const e=document.querySelector(".fusuma-left"),t=document.querySelector(".fusuma-right"),s=document.getElementById("fusuma-overlay");setTimeout(()=>{e.classList.add("open"),t.classList.add("open")},500),s.addEventListener("transitionend",()=>{s.style.display="none"})});document.addEventListener("DOMContentLoaded",()=>{const e=document.getElementById("toggleButton"),t=document.getElementById("character-section");e&&t&&(t.classList.remove("is-night"),e.addEventListener("click",()=>{t.classList.toggle("is-night")}))});const o=document.getElementById("stars-container"),d=250;o.innerHTML="";for(let e=0;e<d;e++){const t=document.createElement("div");t.classList.add("star");const s=Math.random()*4+1;t.style.width=`${s}px`,t.style.height=`${s}px`,t.style.top=`${Math.random()*100}%`,t.style.left=`${Math.random()*100}%`,t.style.animationDelay=`${Math.random()*-15}s`,t.style.animationDuration=`${Math.random()*2+1}s`,o.appendChild(t)}const n=document.querySelector(".shooting-star");function a(){n.style.animation="none",n.offsetHeight;const e=Math.random()*100-50,t=Math.random()*30-10,s=Math.random()*3+2,r=Math.random()*15;n.style.setProperty("--start-x",`${e}vw`),n.style.setProperty("--start-y",`${t}vh`),n.style.setProperty("--end-x",`${e+120}vw`),n.style.setProperty("--end-y",`${t+70}vh`),n.style.setProperty("--rotate-deg",`${Math.random()*60-30}deg`),n.style.animation=`shooting-star-anim ${s}s ease-in forwards`,n.style.animationDelay=`${r}s`}setTimeout(()=>{a(),setInterval(a,8e3+Math.random()*1e4)},3e3);
